let APNG
let count = 0;
const btn = document.getElementById('btn')

if(APNG){
  APNG.ifNeeded().then(function() {
    var images = document.querySelectorAll(".apng-image");
    for (var i = 0; i < images.length; i++) {
      APNG.animateImage(images[i]);
    }
  });
  function startAPNG(){
    console.log('startAPNG')
  }
}

function createAPNG(){
  if(count >0){
    console.log(count)
    return false
  }
  count++
  console.log(count)
  console.log('createAPNG')
  const wrapper = document.querySelector('.wrapper')
  const APNGEle = document.createElement('img')
  APNGEle.setAttribute('class', 'apng-image')
  APNGEle.setAttribute('src', './elephant_apng_zopfli.png')
  wrapper.appendChild(APNGEle)

  // startAPNG()
}

btn.addEventListener("click", createAPNG)

